import { BrowserRouter as Link } from "react-router-dom";

function EventCard({ event: {id, name, date, time, info, image_url} }) {
  return (
    
    <div className="flex items-center mx-auto justify-center p-10">
      {/* <Link to={`/events/${id}`} /> only works in react v5 */} {/* TO BE ADDED LATER */}
      <div className="h-full w-80 bg-[#ffe4c4] rounded-xl overflow-hidden shadow-lg hover:shadow-xl hover:scale-105 duration-500 transform transition cursor-pointer">
        <img className="w-full h-48 object-cover" src={image_url} alt=""/>
        <div className="p-5">
          <h1 className="text-xl font-bold">{name}</h1>
          <p className="mt-2 text-md font-semibold text-gray-600">Date: 16/03/2022</p> {/*{date}*/}
          <p className="mt-2 text-md font-semibold text-gray-600">Time: 7:00am - 11:00am </p> {/* {time} */}
          <p className="mt-2 text-gray-500 font-">Description: {info.substring(0,80)}...</p>
        </div>
      </div> 
    </div>
  );
}
export default EventCard;
