import { BrowserRouter as Link } from "react-router-dom";
import LoggedInAdmin from "../../../apis/LoggedInAdmin";
import { useHistory } from 'react-router-dom';

function AdminEventCard({ event: {event_id, name, date, time, info, image_url} }) {

  let history = useHistory();

  function handleApprove() {
    new LoggedInAdmin().patch(`/events/${this.event_id}/approve`).then(
      (res) => {
        console.log(res)
        console.log(event_id)
        history.push('/admin/home')
      })
  }
  return (
    
    <div className="flex items-center mx-auto justify-center p-10">
      {/* <Link to={`/events/${id}`} /> only works in react v5 */} {/* TO BE ADDED LATER */}
      <div className="h-full w-80 bg-white rounded-xl overflow-hidden shadow-lg hover:shadow-xl hover:scale-105 duration-500 transform transition cursor-pointer">
        <img className="w-full h-48 object-cover" src={image_url} alt=""/> 
        <div className="p-5">
          <h1 className="text-xl font-bold">{name}</h1>
          {/* <p className="mt-2 text-md font-semibold text-gray-600">Date: {date}</p>
          <p className="mt-2 text-md font-semibold text-gray-600">Time: {time}</p> */} {/* TO BE ADDED LATER */}
          <p className="mt-1 text-gray-500 font-">{info.substring(0,70)}...</p>
          <button class="bg-green-400 hover:bg-blue-700 text-white font-bold py-2 px-4 mt-5 ml-4 rounded-full w-2/5" 
         >
            Approve
          </button>
          <button class="bg-red-500 hover:bg-blue-700 text-white font-bold py-2 px-4 mt-5 ml-4 rounded-full w-2/5">
            Reject
          </button>
        </div>
      </div> 
    </div>
  );
}
export default AdminEventCard;
