import React, { Component } from "react";
import axios from "axios";
import Maps from "./Maps";
import { Map, GoogleApiWrapper, Marker } from 'google-maps-react';

class ProposeNewEvent extends Component {
  constructor(props) {
    super(props);
    console.log(this.props); // props will get logged.
    this.state = {
      all_event: [],
    };
  }
//   componentDidMount() {
//     console.log("mounting");
//     // console.log(all_events);
//     this.loadEvents();
//   }

//   loadEvents() {
//       axios.post(
//         "http://localhost:5000/events").then((res) => {
//           this.setState({
//             all_events: res.data.reverse(),
//           });
//         });
//     //  console.log(this.state.all_events)
//   }
  
  render() {
    return (
      <div class="flex pt-32 justify-center">
        <form class="w-full max-w-lg">

        {/* Need to use cookie to get employee ID */}

          <div class="flex flex-wrap -mx-3 mb-6">
            <div class="w-full px-3">
              <label class="block uppercase text-gray-700 text-xs font-bold mb-2" for="grid-name-of-event">
                Event Title
              </label>
              <input class="appearance-none block w-full bg-gray-200 text-gray-700 border border-gray-200 rounded py-3 px-4 mb-3 leading-tight focus:outline-none focus:bg-white focus:border-gray-500" id="grid-name-of-event" type="text" />
            </div>
          </div>

          <div class="flex flex-wrap -mx-3 mb-10">
            <div class="w-full md:w-2/3 px-3 mb-6 md:mb-0">
              <label class="block uppercase tracking-wide text-gray-700 text-xs font-bold mb-2" for="grid-city">
                Address of event
              </label>
              <input class="appearance-none block w-full bg-gray-200 text-gray-700 border border-gray-200 rounded py-3 px-4 leading-tight focus:outline-none focus:bg-white focus:border-gray-500" id="grid-city" type="text" />
            </div>
            
            <div class="w-full md:w-1/3 px-3 mb-6 md:mb-0">
              <label class="block uppercase tracking-wide text-gray-700 text-xs font-bold mb-2" for="grid-zip">
                Postal code
              </label>
              <input class="appearance-none block w-full bg-gray-200 text-gray-700 border border-gray-200 rounded py-3 px-4 leading-tight focus:outline-none focus:bg-white focus:border-gray-500" id="grid-zip" type="text" />
            </div>

            <div>
            <Maps
              google={this.props.google}
              zoom={8}
              style={mapStyles}
              initialCenter={{ lat: 9.761927, lng: 79.95244 }}
            >
              <Marker position={{ lat: 9.761927, lng: 79.95244 }} />
            </Maps>
            </div>
            
          </div>

          <div class="flex flex-wrap -mx-3 mb-6">
            <div class="w-full px-3">
              <label class="block uppercase text-gray-700 text-xs font-bold mb-2" for="grid-details">
                Details (This will be available to everyone, even for those who have not signed up)
              </label>
              <input class="appearance-none block w-full bg-gray-200 text-gray-700 border border-gray-200 rounded py-24 px-4 mb-3 leading-tight focus:outline-none focus:bg-white focus:border-gray-500" id="grid-details" type="text" />
            </div>
          </div>

          <div class="flex flex-wrap -mx-3 mb-6">
            <div class="w-full px-3">
              <label class="block uppercase text-gray-700 text-xs font-bold mb-2" for="grid-details">
                Proposal Details (Only available to admin for approval purposes)
              </label>
              <input class="appearance-none block w-full bg-gray-200 text-gray-700 border border-gray-200 rounded py-24 px-4 mb-3 leading-tight focus:outline-none focus:bg-white focus:border-gray-500" id="grid-details" type="text" />
            </div>
          </div>

          <div class="flex flex-wrap -mx-3 mb-6">
            <div class="w-full md:w-1/2 px-3 mb-6 md:mb-0">
              <label class="block uppercase tracking-wide text-gray-700 text-xs font-bold mb-2" for="grid-first-name">
                Registration opens on
              </label>
              <input class="appearance-none block w-full bg-gray-200 text-gray-700 border border-gray-200 rounded py-3 px-4 mb-3 leading-tight focus:outline-none focus:bg-white" id="grid-first-name" type="text" />
              {/* <p class="text-red-500 text-xs italic">Please fill out this field.</p> */}
            </div>
            <div class="w-full md:w-1/2 px-3">
              <label class="block uppercase tracking-wide text-gray-700 text-xs font-bold mb-2" for="grid-last-name">
              Registration closes on
              </label>
              <input class="appearance-none block w-full bg-gray-200 text-gray-700 border border-gray-200 rounded py-3 px-4 leading-tight focus:outline-none focus:bg-white focus:border-gray-500" id="grid-last-name" type="text" />
            </div>
          </div>
          
          <div class="flex flex-wrap -mx-3 mb-6">
            <div class="w-full px-3">
              <label class="block uppercase text-gray-700 text-xs font-bold mb-2" for="grid-details">
                Images
              </label>
              <input class="appearance-none block w-full bg-gray-200 text-gray-700 border border-gray-200 rounded py-24 px-4 mb-3 leading-tight focus:outline-none focus:bg-white focus:border-gray-500" id="grid-details" type="text" />
            </div>
          </div>

          <div class="flex flex-wrap -mx-3 mb-6">
            <div class="w-full px-3 mb-6 md:mb-0">
              <label class="block uppercase text-gray-700 text-xs font-bold mb-2" for="grid-tags">
                Tags
              </label>
              <div class="relative mb-2">
                <select class="block appearance-none w-full bg-gray-200 border border-gray-200 text-gray-700 py-3 px-4 pr-8 rounded leading-tight focus:outline-none focus:bg-white focus:border-gray-500" id="grid-state">
                  <option>New Mexico</option>
                  <option>Missouri</option>
                  <option>Texas</option>
                </select>
              </div>
            </div>
          </div>

        </form>
      </div>
    );
  }
}

export default ProposeNewEvent;
